          state.discussionsLast[index].is_favorite = is_favorite
        }
      })
    },//*
    replaceDiscussionTop(state, { id, is_favorite }) {
      state.discussionsTop.find((disc, index) => {
        if (disc.id == id) {
          state.discussionsTop[index].is_favorite = is_favorite
        }
