            items_per_page: res.data.items_per_page,
            itemsPerPage: 1
          })
          return res.data
        })
      } else if (!state.discussionsAll.some(item => {
        return item.page == page
      })) {
        return GetAllDiscussion(page).then(res => {
          commit('setAllDiscusion', { items: res.data.items, page })
          return true
        })
      }

