export default {
  namespaced: true,
  state: {
    authors_discussion: [],
    paginationSetting:{
      total_pages: null,
      total_items: null,
      items_per_page: null,
    }
  },
  mutations: {
    clereAuthorsDiscussions(state){
      state.authors_discussion = []
      state.paginationSetting = {
        total_pages: null,
        total_items: null,
        items_per_page: null,
      }
    },
    setAuthorsDiscussions(state, payload){
      state.authors_discussion.push({items: payload.items, page: payload.page})
      state.paginationSetting = {
        total_pages: payload.total_pages,
        total_items: payload.total_items,
        items_per_page: payload.items_per_page,
      }
    }
  },
  actions:{
    getAuthorDiscussions({commit}, {id, page}){
      return GetAuthorDiscussions({id, page}).then(res => {
        commit('setAuthorsDiscussions', res.data)
        return res.data
      })
    }
  }

}

import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)
Vue.config.devtools = true
import modules from './store/modules'

export function createStore () {
  return new Vuex.Store({
    state () {
      return {
        menu: false,
        dialog: false,
        alertMessage: '',
        alertType: null
      }
    },
    mutations: {
      openMenu(state, status) {
        state.menu = status
      },
      openDialog(state, payload){
        state.dialog = true
        if(typeof payload === 'string'){
          state.alertMessage = payload
          return
        }
        if(payload.hasOwnProperty('type')){
          state.alertMessage = payload.message
          state.alertType = payload.type
        }else{
          state.alertMessage = payload.message
        }

      },
      closeDialog(state){
        state.dialog = false
        state.alertMessage = ''
        state.alertType = null
      },
      setDialog(state, payload){
        state.dialog = payload
      },
    },

    actions: {
    },

    modules
  })
}

